console.log("Hallo");
console.log("Tolle Sache");

// Variablen
let a = 7;
let b = 8;
let c = 10;

// Berechne folgendes und gib das Ergebnis über console.log aus
console.log("Erste Berechnungen:");
// a + b + c
console.log(a + b + c);

// (a + b) / c
console.log((a + b) / c);

// c - a + b
console.log(c - a + b);

// a + b + c * 3
console.log(a + b + c * 3);

// (a + b + c)/4
console.log((a + b + c) / 4);

// Ändere die Variablen auf folgende Werte
a = 3;
b = a + 3;
c = c - 3;

// gib die neuen Werte der Variablen aus
console.log("Neue Werte der Variablen:");
console.log("a:", a);
console.log("b:", b);
console.log("c:", c);